export const wrapExpectedWithArray = (el, actual, expected) => {
    if (Array.isArray(el) && el.length > 1 && Array.isArray(actual)) {
        expected = [expected];
    }
    return expected;
};
export const updateElementsArray = (success, received, refetched, full = false) => {
    if (!success) {
        return;
    }
    if (Array.isArray(received) && Array.isArray(refetched)) {
        if (full === true) {
            while (received.length > 0) {
                received.pop();
            }
        }
        if (received.length === 0) {
            for (let i = 0; i < refetched.length; i++) {
                received.push(refetched[i]);
            }
        }
    }
};
